CREATE TABLE `hasstore`.`products` ( `id` INT NOT NULL AUTO_INCREMENT , `year` INT NOT NULL , `semester` INT NOT NULL , `department` VARCHAR(100) NOT NULL , `university` VARCHAR(100) NOT NULL , `subject` VARCHAR(200) NOT NULL , `name` VARCHAR(200) NOT NULL , `author` VARCHAR(200) NOT NULL , `cost` INT NOT NULL , `publisher` VARCHAR(100) NOT NULL , `description` INT(10000) NOT NULL , `imgurl` VARCHAR(100) NOT NULL , `edition` INT NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB COMMENT = 'Main Products Tabke'; 